import{_ as a}from"./_plugin-vue_export-helper.c27b6911.js";import{o as t,c as e,f as o}from"./entry.a437b066.js";const l={},i={class:"my-5 rounded"},s=o('<h2 class="font-bold text-xl mb-2 text-gray-700 text-center" data-v-8f829065>Robust</h2><div data-v-8f829065><div class="px-1 mx-1 py-2" data-v-8f829065><nav class="accordion arrows" data-v-8f829065><input type="radio" name="accordion" id="cb1" data-v-8f829065><section class="box" data-v-8f829065><label class="box-title dark:text-slate-900" for="cb1" style="width:100%;" data-v-8f829065>Guideline 4.1 Compatible</label><label class="box-close" for="acc-close" data-v-8f829065></label><div class="box-content m-2" data-v-8f829065><span class="text-gray-700" data-v-8f829065> Maximize compatibility with current and future user agents, including assistive technologies.</span><ul class="text-gray-700 mt-5" data-v-8f829065><li data-v-8f829065> Ensuring markup can be reliably interpreted, for instance by ensuring it is valid </li><li data-v-8f829065> Providing a name, role, and value for non-standard user interface components </li></ul></div></section><input type="radio" name="accordion" id="cb2" data-v-8f829065><section class="box" data-v-8f829065><label class="box-title dark:text-slate-900" for="cb2" style="width:100%;" data-v-8f829065>Guideline 4.3.8 Checking headings </label><label class="box-close" for="acc-close" data-v-8f829065></label><div class="box-content m-2" data-v-8f829065><span class="text-gray-700" data-v-8f829065> Another way to check headings is by using the WAVE tool. </span><ul class="text-gray-700 mt-5" data-v-8f829065><li data-v-8f829065>Open WAVE Web Accessibility Evaluation Tool.</li><li data-v-8f829065>Type the web page address in the form field</li><li data-v-8f829065>Select the [WAVE this page!] button or press enter.</li><li data-v-8f829065>The web page will show up in the browser with little icons on it.</li><li data-v-8f829065> Anything that looks visually like a heading should have a heading icon (H1 WAVE Tool Icon, H2 WAVE Tool Icon, H3 WAVE Tool Icon, etc.) before it. </li><li data-v-8f829065> Anything that does not visually look like heading should not have a heading icon before it. </li></ul></div></section><input type="radio" name="accordion" id="cb3" data-v-8f829065><section class="box" data-v-8f829065><label class="box-title dark:text-slate-900" for="cb3" style="width:100%;" data-v-8f829065>Guideline 4.3.12 Contrast ratio </label><label class="box-close" for="acc-close" data-v-8f829065></label><div class="box-content m-2" data-v-8f829065><span class="text-gray-700" data-v-8f829065> Some examples where contrast ratio are important include: text, charts, buttons, icons, and really any visual element where information needs to be conveyed. </span><ul class="text-gray-700 mt-5" data-v-8f829065><li data-v-8f829065> For WCAG specific rules the contrast ratio requirements are at least four point five to one for regular size text while large text and non text content such as form elements and graphical buttons require a contrast ratio of at least three to one. But don&#39;t worry you don&#39;t have to calculate anything. There are a lot of great tools available to help you calculate the contrast ratio. </li></ul></div></section><input type="radio" name="accordion" id="acc-close" data-v-8f829065></nav></div></div>',2),n=[s];function c(d,r){return t(),e("div",i,n)}const u=a(l,[["render",c],["__scopeId","data-v-8f829065"]]);export{u as default};
